import React, { Component } from 'react'
import Casilla from '../Casilla/Casilla';
import './Tablero.css';

export default class Tablero extends Component {

    classVar = "portada";

    sayHello() {
        console.log("Hello")
        this.classVar = "contenido"
    }

    render(){
        return (
            <div className="tablero">          
                {
                    this.props.casillas
                    .map((casilla,index) => {
                        return <Casilla
                        sayHello={()=>this.sayHello()}
                        classCss={this.classVar}
                        />
                    })
                }
            </div>  
        )
    }

}
