const NUMERO_DE_CASILLAS = 20
const NUMERO_DE_MINAS = 5

export default () => {

    let casillas = [];
    let minas_puestas = 0;

    for(let i=0; i<NUMERO_DE_CASILLAS; i++){
        let casilla = {
            es_mina : false
        }
        casillas.push(casilla);
    }

    while(minas_puestas < NUMERO_DE_MINAS){
        const index = Math.random() * (NUMERO_DE_CASILLAS-1)
        console.log(index)
        if(!casillas[index].es_mina){
            casillas[index].es_mina = true;
            minas_puestas++;
        }
    }

    return casillas
}
