const NUMERO_DE_CASILLAS = 20
const NUMERO_DE_MINAS = 5

export default () => {

    let casillas = [];
    let minas_puestas = 0;

    for(i=0; i<NUMERO_DE_CASILLAS; i++){
        casillas.push({es_mina:false})
    }

    while(minas_puestas < NUMERO_DE_MINAS){
        const index = Math.random() * NUMERO_DE_CASILLAS
        if(!casillas[index]){
            casillas[index].es_mina = true;
            minas_puestas++;
        }
    }

    return casillas
}
